<script>
	import QrcodeVue from 'qrcode.vue'
	export default {
		components: {
			QrcodeVue,
		},
		mounted() {
			this.getData();
		},
		data: () => ({
			abiturientNotification: '',
			QRToken: null,
			QRDialog: false,
		}),
		computed: {
			selectedLanguage() {
				return this.$store.getters.getLanguage;
			},
			abiturientFio() {
				let fio = (this.$store.getters.getAbiturientInfo.surname || '')+' '+(this.$store.getters.getAbiturientInfo.name || '')+' '+(this.$store.getters.getAbiturientInfo.lastname || '');
				/*document.title = this.$store.getters.getCollegeInfo.college_name;*/
				return fio;
			}
		},
		methods: {
			async getData() {
				await this.$http.get('/notification').then((response) => {
					if(response.data.data) {
						this.abiturientNotification = response.data.data.text;
					}
				});
			},
			async showQRDialog() {
				if(this.QRToken == null) {
					await this.getQR();
				}
				this.QRDialog = true;
			},
			async getQR() {
				await this.$http.get('/qr').then((response) => {
					this.QRToken = response.data.data.token;
				});
			},
		}
	}
</script>

